mapper.select:



		AND title like concat('%', #{operLog.title}, '%')


		AND business_type = #{operLog.businessType}


	    AND business_type in

 			#{operLog.businessType}



		AND status = #{operLog.status}


		AND oper_name like concat('%', #{operLog.operName}, '%')


		and date_format(operLog.oper_time,'%y%m%d') >= date_format(#{operLog.beginTime},'%y%m%d')


		and date_format(operLog.oper_time,'%y%m%d') <= date_format(#{operLog.endTime},'%y%m%d')


order by oper_id desc
